import java.util.*;
public class Add2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n1;
		int n2;
		
		System.out.print("첫 번째 숫자를 입력하시오: ");
		n1 = sc.nextInt();
		System.out.print("두 번째 숫자를 입력하시오: ");
		n2 = sc.nextInt();
		
		System.out.println(n1+n2);
	}

}
